#include <stdio.h>

int main() {
    int a, b, c, max;
    scanf("%d %d %d", &a, &b, &c);

    if (a >= b && a >= c) {
        max = a;
    } else if (b >= a && b >= c) {
        max = b;
    } else {
        max = c;
    }

    printf("%d\n", max);
    return 0;
}

#include <stdio.h>

int linearSearch(int arr[], int n, int x)
{
    for (int i = 0; i < n; i++)
    {
        if (arr[i] == x)
            return i;
    }
    return -1;
}

int main()
{
    int n, x;
    scanf("%d", &n); // read array size from input
    int arr[n];
    for (int i = 0; i < n; i++)
        scanf("%d", &arr[i]); // read array elements from input
    scanf("%d", &x); // read target element from input
    int index = linearSearch(arr, n, x);
    if (index == -1)
        printf("-1"); // target element not found
    else
        printf("%d", index); // target element found at index
    return 0;
}
